from socket import *
import os
import time

# serverName = 'hostname'
serverName = '192.168.1.135'
serverPort = 12000


eofMsg = "####"
msg = ""

# fills multiple pakages to send off
payload = [str(x+1)+";"+eofMsg for x in range(10000,11000)]

print("The time of code execution begin is : ", time.ctime())

# create TCP socket on client to use for connecting to remote server
clientSocket = socket(AF_INET, SOCK_STREAM)

# open the TCP connection
clientSocket.connect((serverName, serverPort))

# loop to send all pakages 
for i in range(1000):  
    clientSocket.send(payload[i].encode())
    time.sleep(0.02)  

print("The time of code execution end is : ", time.ctime())

# so I can see output before the windo closes
os.system("pause")

# close the TCP connection
clientSocket.close()

