from socket import *
import time
import os
# serverName = 'hostname'
serverName = '192.168.1.135'
serverPort = 12000

# create UDP socket
clientSocket = socket(AF_INET, SOCK_DGRAM)

#message info
eofMsg = "####"
msg = ""

# fills with multiple packages for send of
payload = [str(x+1)+";"+msg+eofMsg for x in range(10000,11000)]

# loop to send all packages
for i in range(1000):  
    clientSocket.sendto(payload[i].encode(),(serverName, serverPort))
    time.sleep(0)  

# so that i can see the output before the window closes
os.system("pause")

# close UDP socket
clientSocket.close()
